import React, {useEffect} from 'react'
import {NavLink} from 'react-router-dom'

import useFetch from 'hooks/useFetch'
import UserArticles from 'components/UserArticles'

const UserProfile = ({location, match:{url, params:{slug}}}) => {
    const isFavorites = location.pathname.includes('favorites')

    const [{response}, doFetch] = useFetch(`/profiles/${slug}`)

    useEffect(() => {

        doFetch()
    }, [doFetch])

    if (!response) {
        return null
    }

    return (
        <div className="profile-page">
            <div className="user-info">
                <div className="container">
                    <div className="row">
                        <div className="col-xs-12 col-md-10 offset-md-1">
                            <img src={response.profile.image} alt="" className="user-image"/>
                            <h4>{response.profile.username}</h4>
                            <p>{response.profile.bio}</p>
                        </div>
                    </div>
                </div>
            </div>

            <div className="container">
                <div className="row">
                    <div className="col-xs-12 col-md-10 offset-md-1">
                        <div className="articles-toggle">
                            <ul className="nav nav-pills outline-active">
                                <li className="nav-item">
                                    <NavLink exact className="nav-link" to={`/profiles/${response.profile.username}`}>
                                        My posts
                                    </NavLink>
                                </li>
                                <li className="nav-item">
                                    <NavLink className="nav-link" to={`/profiles/${response.profile.username}/favorites`}>
                                        Favorite posts
                                    </NavLink>
                                </li>
                            </ul>
                        </div>
                        <UserArticles username={response.profile.username} location={location} isFavorites={isFavorites} url={url}/>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default UserProfile
